tests:
Test.assertEquals(classifyRug([
	['a', 'a', 'b'],
  ['a', 'a', 'b']
]), "horizontally symmetric");

Test.assertEquals(classifyRug([
  ['a']
]), "perfect");

Test.assertEquals(classifyRug([
  ['a'],
  ['b'], 
  ['a']
]), "perfect");

Test.assertEquals(classifyRug([
  ['a'],
  ['b'], 
  ['c']
]), "vertically symmetric");

Test.assertEquals(classifyRug([
  ['a', 'b', 'a']
]), "perfect");

Test.assertEquals(classifyRug([
  ['a', 'a']
]), "perfect");

Test.assertEquals(classifyRug([
  ['d', 'a'],
  ['b', 'b'],
  ['d', 'a']
]), "horizontally symmetric");

Test.assertEquals(classifyRug([
  ['d', 'a', 'a', 'a'],
  ['b', 'b', 'a', 'a'],
  ['d', 'a', 'a', 'a']
]), "horizontally symmetric");

Test.assertEquals(classifyRug([
  ['a', 'a', 'a', 'a'],
  ['d', 'a', 'a', 'a']
]), "imperfect");
