tests:
Test.assertSimilar(
  spiral(
    [
      [7, 2],
      [5, 0]
    ]
  ),
  [7, 2, 0, 5],
  'Should work with a 2x2 array'
);

Test.assertSimilar(
  spiral(
    [
      [1, 2, 3],
      [4, 5, 6],
      [7, 8, 9]
    ]
  ),
  [1, 2, 3, 6, 9, 8, 7, 4, 5],
  'Should work with a 3x3 array'
);

Test.assertSimilar(
  spiral(
    [
      [1, 9, 9],
      [2, 4, 9],
      [7, 3, 9]
    ]
  ),
  [1, 9, 9, 9, 9, 3, 7, 2, 4],
  'Should work with a 3x3 array'
);

Test.assertSimilar(
  spiral(
    [
      [6, 4, 3, 9],
      [2, 5, 7, 1],
      [8, 6, 2, 3],
      [4, 5, 9, 1]
    ]
  ),
  [6, 4, 3, 9, 1, 3, 1, 9, 5, 4, 8, 2, 5, 7, 2, 6],
  'Should work with a 4x4 array'
);

Test.assertSimilar(
  spiral(
    [
      [4, 1, 55, 5, 9],
      [16, 7, 6, 5, 3],
      [8, 20, 2, 0, 8],
      [2, 9, 9, 1, 11],
      [6, 5, 3, 18, 5]
    ]
  ),
  [4, 1, 55, 5, 9, 3, 8, 11, 5, 18, 3, 5, 6, 2, 8, 16, 7, 6, 5, 0, 1, 9, 9, 20, 2],
  'Should work with a 5x5 array'
);

Test.assertSimilar(
  spiral(
    [
      [1, 5],
      [9, 2],
      [7, 3]
    ]
  ),
  [1, 5, 2, 3, 7, 9],
  'Should work when there are more rows than collumns'
);

Test.assertSimilar(
  spiral(
    [
      [2, 4, 6, 8],
      [7, 3, 0, 5]
    ]
  ),
  [2, 4, 6, 8, 5, 0, 3, 7],
  'Should work when there are more collumns than rows'
);
